    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        '\n    You are given a 0-indexed integer array stations of length n, where stations[i] represents the number of power stations in the ith city.\n    Each power station can provide power to every city in a fixed range. In other words, if the range is denoted by r, then a power station at city i can provide power to all cities j such that |i - j| <= r and 0 <= i, j <= n - 1.\n    Note that |x| denotes absolute value. For example, |7 - 5| = 2 and |3 - 10| = 7.\n    The power of a city is the total number of power stations it is being provided power from.\n    The government has sanctioned building k more power stations, each of which can be built in any city, and have the same range as the pre-existing ones.\n    Given the two integers r and k, return the maximum possible minimum power of a city, if the additional power stations are built optimally.\n    Note that you can build the k power stations in multiple cities.\n    '
        n = len(stations)
        power = ([0] * n)
        for i in range(n):
            for j in range(max(0, (i - r)), min(n, ((i + r) + 1))):
                power[j] += stations[i]

        def updated_power(idx: int) -> List[int]:
            new_power = power.copy()
            for j in range(max(0, (idx - r)), min(n, ((idx + r) + 1))):
                new_power[j] += 1
            return new_power

        def find_optimal_placement(k: int) -> List[int]:
            if (k == 0):
                return power
            best_min_power = 0
            best_power = []
            for i in range(n):
                new_power = updated_power(i)
                remaining_power = find_optimal_placement((k - 1))
                combined_power = [max(a, b) for (a, b) in zip(new_power, remaining_power)]
                min_power = min(combined_power)
                if (min_power > best_min_power):
                    best_min_power = min_power
                    best_power = combined_power
            return best_power
        optimal_power = find_optimal_placement(k)
        return min(optimal_power)

class Solution():

    def countAnagrams(self, s: str) -> int:
        '\n    You are given a string s containing one or more words. Every consecutive pair of words is separated by a single space \' \'.\n    A string t is an anagram of string s if the ith word of t is a permutation of the ith word of s.\n    For example, "acb dfe" is an anagram of "abc def", but "def cab"\xa0and "adc bef" are not.\n    Return the number of distinct anagrams of s. Since the answer may be very large, return it modulo 109 + 7.\n    '
        from collections import Counter
        from math import factorial
        from functools import reduce
        import operator
        MOD = ((10 ** 9) + 7)

        def count_permutations(word: str) -> int:
            counter = Counter(word)
            return (factorial(len(word)) // reduce(operator.mul, (factorial(v) for v in counter.values()), 1))
        words = s.split()
        sorted_words = [''.join(sorted(word)) for word in words]
        word_permutations = [count_permutations(word) for word in sorted_words]
        total_permutations = factorial(len(words))
        total_anagrams = total_permutations
        for word_perm in word_permutations:
            total_anagrams //= word_perm
        return (total_anagrams % MOD)

