            for j in range(6):
                dp[i][i][j] = 1
        for length in range(1, (n + 1)):
            for i in range(((n - length) + 1)):
                j = (i + length)
                for k in range(1, 6):
                    dp[i][j][k] = ((dp[(i + 1)][j][k] + dp[i][(j - 1)][k]) - dp[(i + 1)][(j - 1)][k])
                    if (s[i] == s[(j - 1)]):
                        if ((k == 1) or (k == 5)):
                            dp[i][j][k] += dp[(i + 1)][(j - 1)][(k - 1)]
                        else:
                            dp[i][j][k] += (dp[(i + 1)][(j - 1)][(k - 1)] - dp[(i + 1)][(j - 1)][k])
                    dp[i][j][k] %= MOD
        return dp[0][n][5]

