    def beautifulPartitions(self, s: str, k: int, minLength: int) -> int:
        "\n    You are given a string s that consists of the digits '1' to '9' and two integers k and minLength.\n    A partition of s is called beautiful if:\n    s is partitioned into k non-intersecting substrings.\n    Each substring has a length of at least minLength.\n    Each substring starts with a prime digit and ends with a non-prime digit. Prime digits are '2', '3', '5', and '7', and the rest of the digits are non-prime.\n    Return the number of beautiful partitions of s. Since the answer may be very large, return it modulo 109 + 7.\n    A substring is a contiguous sequence of characters within a string.\n    "
        MOD = ((10 ** 9) + 7)
        prime_digits = {'2', '3', '5', '7'}
        non_prime_digits = {'1', '4', '6', '8', '9'}

        def is_beautiful(substring: str) -> bool:
            return ((len(substring) >= minLength) and (substring[0] in prime_digits) and (substring[(- 1)] in non_prime_digits))

        def count_beautiful_partitions(start: int, remaining_partitions: int) -> int:
            if (remaining_partitions == 0):
                return (1 if (start == len(s)) else 0)
            if (start >= len(s)):
                return 0
            count = 0
            for end in range(((start + minLength) - 1), len(s)):
                if is_beautiful(s[start:(end + 1)]):
                    count += count_beautiful_partitions((end + 1), (remaining_partitions - 1))
                    count %= MOD
            return count
        memo = {}

        def memoized_count_beautiful_partitions(start: int, remaining_partitions: int) -> int:
            if ((start, remaining_partitions) in memo):
                return memo[(start, remaining_partitions)]
            count = count_beautiful_partitions(start, remaining_partitions)
            memo[(start, remaining_partitions)] = count
            return count
        return memoized_count_beautiful_partitions(0, k)

class Solution():

    def splitMessage(self, message: str, limit: int) -> List[str]:
        '\n    You are given a string, message, and a positive integer, limit.\n    You must split message into one or more parts based on limit. Each resulting part should have the suffix "<a/b>", where "b" is to be replaced with the total number of parts and "a" is to be replaced with the index of the part, starting from 1 and going up to b. Additionally, the length of each resulting part (including its suffix) should be equal to limit, except for the last part whose length can be at most limit.\n    The resulting parts should be formed such that when their suffixes are removed and they are all concatenated in order, they should be equal to message. Also, the result should contain as few parts as possible.\n    Return the parts message would be split into as an array of strings. If it is impossible to split message as required, return an empty array.\n    '
        from typing import List
        if (limit < 5):
            return []
        message_length = len(message)
        max_parts = (((message_length + limit) - 5) // (limit - 4))
        if (message_length > (max_parts * (limit - 4))):
            return []
        parts = []
        index = 1
        while message:
            part_length = (((limit - len(str(index))) - len(str(max_parts))) - 3)
            if (len(message) <= part_length):
                part = message
                message = ''
            else:
                part = message[:part_length]
                message = message[part_length:]
            parts.append((part + f'<{index}/{max_parts}>'))
            index += 1
        return parts

