// 0 <= i < j < k < l < n, and
// nums[i] < nums[k] < nums[j] < nums[l].
fn count_quadruplets(nums: Vec<i32>) -> i64 {
    let n = nums.len();
    let mut count = 0;

    for i in 0..n {
        for j in i + 1..n {
            for k in j + 1..n {
                for l in k + 1..n {
                    if nums[i] < nums[k] && nums[k] < nums[j] && nums[j] < nums[l] {
                        count += 1;
                    }
                }
            }
        }
