// For example, "acb dfe" is an anagram of "abc def", but "def cab" and "adc bef" are not.
// Return the number of distinct anagrams of s. Since the answer may be very large, return it modulo 109 + 7.
fn count_anagrams(s: String) -> i32 {

solution:
// You are given a string s containing one or more words. Every consecutive pair of words is separated by a single space ' '.
// A string t is an anagram of string s if the ith word of t is a permutation of the ith word of s.
// For example, "acb dfe" is an anagram of "abc def", but "def cab" and "adc bef" are not.
// Return the number of distinct anagrams of s. Since the answer may be very large, return it modulo 109 + 7.
fn count_anagrams(s: String) -> i32 {
    let words: Vec<&str> = s.split(' ').collect();
    let modulo = 1_000_000_007;
    let mut result = 1;

    for word in words {
        let mut char_count = vec![0; 26];
        for c in word.chars() {
            char_count[(c as u8 - b'a') as usize] += 1;
        }

        let mut word_permutations = 1;
        let mut total_chars = 0;
        for count in char_count {
            if count > 0 {
                total_chars += count;
                word_permutations = (word_permutations * factorial(count, modulo)) % modulo;
            }
        }

        let total_permutations = factorial(total_chars, modulo);
        let word_anagrams = (total_permutations * mod_inverse(word_permutations, modulo)) % modulo;
        result = (result * word_anagrams) % modulo;
    }

    result
}

fn factorial(n: i32, modulo: i32) -> i32 {
    let mut result = 1;
    for i in 1..=n {
        result = (result * i) % modulo;
    }
    result
}

fn mod_inverse(a: i32, modulo: i32) -> i32 {
    let mut a = a % modulo;
    for x in 1..modulo {
        if (a * x) % modulo == 1 {
            return x;
        }
    }
    1
}

