fn number_of_paths(grid: Vec<Vec<i32>>, k: i32) -> i32 {

solution:
// You are given a 0-indexed m x n integer matrix grid and an integer k. You are currently at position (0, 0) and you want to reach position (m - 1, n - 1) moving only down or right.
// Return the number of paths where the sum of the elements on the path is divisible by k. Since the answer may be very large, return it modulo 109 + 7.
fn number_of_paths(grid: Vec<Vec<i32>>, k: i32) -> i32 {
    let m = grid.len();
    let n = grid[0].len();
    let modulo = 1_000_000_007;

    let mut dp = vec![vec![vec![0; k as usize]; n]; m];

    dp[0][0][grid[0][0] as usize % k as usize] = 1;

    for i in 0..m {
        for j in 0..n {
            for p in 0..k as usize {
                if i > 0 {
                    dp[i][j][p] += dp[i - 1][j][(p + k as usize - grid[i][j] as usize % k as usize) % k as usize];
                    dp[i][j][p] %= modulo;
                }
                if j > 0 {
                    dp[i][j][p] += dp[i][j - 1][(p + k as usize - grid[i][j] as usize % k as usize) % k as usize];
                    dp[i][j][p] %= modulo;
                }
            }
        }
    }

    dp[m - 1][n - 1][0]
}
